import fs from "fs";
import path from "path";
import { createTsConfigJson } from "./CreateTsConfig.js";
export function createExpressApp(appName, destinationPath, print) {
    // Use the destinationPath directly
    const root = destinationPath;
    // Create src + subfolders
    const folders = [
        "src/routes",
        "src/controllers",
        "src/middleware",
        "src/services",
        "src/models",
        "src/utils",
    ];
    folders.forEach((folder) => {
        fs.mkdirSync(path.join(root, folder), { recursive: true });
    });
    // Generate tsconfig.json
    createTsConfigJson(root);
    print.success(print.colors.green(`âœ… Express app '${appName}' scaffolded successfully!`));
}
