import fs from "fs";
import { join } from "path";
function generatePackageJson(templatePath, destination, projectName) {
    const pkgPath = join(templatePath, "package.json");
    let pkgContent = fs.readFileSync(pkgPath, "utf-8");
    // Replace placeholder with actual project name
    pkgContent = pkgContent.replace(/{{projectName}}/g, projectName);
    fs.writeFileSync(join(destination, "package.json"), pkgContent);
}
export default generatePackageJson;
