import { execSync } from "child_process";
export function installDependencies(destinationPath, packageManager, print) {
    try {
        print.info(print.colors.info("Installing dependencies... "));
        execSync(`${packageManager} install`, {
            stdio: "inherit",
            cwd: destinationPath,
        });
        print.success(print.colors.green("Dependencies installed successfully!"));
    }
    catch (err) {
        print.error(`Failed to install dependencies: ${err}`);
    }
}
